package pack12;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i=1,fact=1,num=4;
while(i<=num) {
	fact=fact*1;
	i++;
	                              
}
System.out.println("Factorial"+fact);
	}

}
